package week1.day2;

public class IfElsePractice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int mark = 360;
		if(mark>400) {
			System.out.println("The student is a scholar student.");
		}
		else if(mark>320) {
			System.out.println("The student is an average student.");
		}
		else {
			System.out.println("The student needs to study harder.");
		}

	}

}
